import { useState } from "react";
import shoppingIcon from "./assets/shopping-icon.svg";
import './App.css';

function App() {
  const [value, setValue] = useState('');
  const [todos, setTodos] = useState([
    {title : 'Susu Ultra',count :1},
    {title : 'Susu Dancow',count :2},
    {title : 'Susu Entrasol',count :3}
  ]);

  return (
    // React Fragment
    <>
      <nav className="nav">
        <img className="nav-icon" src={shoppingIcon} alt="Shopping icon" />
        <h1 className="nav-title">Shopping List</h1>
      </nav>

      <section className="container">
        <form className="form">
          <input
          onChange={(e) => {setValue(e.target.value)}}
          value={value}
          className="input"
          type="text"
          placeholder="List"
          ></input>
          <button className="add-button" type="submit" onClick={() => {setTodos.push({title:'test',count:4})}}>add</button>
        </form>
        {todos.length > 0 ? (
          <div className="todos">
            {todos.map((todo) => {
              return (
                <div>
                  {todo.title}
                  {todo.count}
                </div>
              )
            })}
          </div>
        ) :  (
          <div>Kosong</div>
        )}
      </section>
    </>
  );
}

export default App;
