import { useState } from "react";
import classNames from "classnames"; //lib untuk css dengan pendekatan JS

import PlusIcon from "./assets/plus-icon.svg";
import MinusIcon from "./assets/minus-icon.svg";
import './App.css';
import Navbar from "./components/Navbar";
function App() {
  const [value, setValue] = useState('');
  const [todos, setTodos] = useState([]);



  const handleSubmit = (e) => {
    e.preventDefault() //mencegah default behavior submit ketika refresh browser

    if(!value){
      return alert('Data tidak boleh kosong!')
    }
    const addedTodos = [...todos, {
      title : value,
      count : 1
    }]
    setTodos(addedTodos)
    setValue('')
  }

  const handleAdditionCount = (index,action) => {
    const newTodos = [...todos]; //untuk mengcopy array ke dalam variabel baru

    if(action == 'plus'){
      newTodos[index].count = newTodos[index].count + 1
    }else{
      if(newTodos[index].count > 0){
        newTodos[index].count = newTodos[index].count - 1
      }else{
        newTodos.splice(index,1) //Splice untuk menghapus data pada array (index,jumlah data yang dihapus)
      }
    }
    setTodos(newTodos);
  }

  const getTotalCounts = () => {
    const totalCounts = todos.reduce((total, num) => {
      return total + num.count
    },0)
    return totalCounts
  } 

  return (
    // React Fragment
    <>


      <section className="container">
        <form className="form" onSubmit={handleSubmit}>
          <input
          onChange={(e) => {setValue(e.target.value)}}
          value={value}
          className="input"
          type="text"
          placeholder="List"
          ></input>
          <button className="add-button" type="submit">add</button>
        </form>

        <div className="info">
          <div className="info-total">
            <p>Total List : {todos.length}</p>
          </div>

          <div className="info-total">
            <p>Total Counts : {getTotalCounts()}</p>
          </div>

          <button className="delete-all-button" onClick={() => setTodos([])}>
            Delete All List
          </button>
        </div>    

        {todos.length > 0 ? (
          <div className="todos">
            {todos.map((todo,index,arr) => {
              return (
                <div key={index} className={`todo ${!(arr.length == index + 1) && 'todo-divider'}`}>
                  {todo.title}
                  <div className="todo-icon-wrapper">
                    <div className="todo-count">{todo.count}</div>
                    <button className="todo-action-button" onClick={() => handleAdditionCount(index,'minus')}>
                      <img src={MinusIcon} alt="minus icon" />
                    </button>
                    <button className="todo-action-button" onClick={() => handleAdditionCount(index,'plus')}>
                      <img src={PlusIcon} alt="plus icon" />
                    </button>
                  </div>
                </div>
              )
            })}
          </div>
        ) :  (
          <div className="todos-empty">
            <p>List data kosong</p>
          </div>
        )}
      </section>
    </>
  );
}

export default App;
