import { useState } from "react";
import classNames from "classnames"; //lib untuk css dengan pendekatan JS

import shoppingIcon from "./assets/shopping-icon.svg";
import PlusIcon from "./assets/plus-icon.svg";
import MinusIcon from "./assets/minus-icon.svg";
import './App.css';

function App() {
  const [value, setValue] = useState('');
  const [todos, setTodos] = useState([
    {title : 'Susu Ultra',count :1},
    {title : 'Susu Dancow',count :2},
    {title : 'Susu Entrasol',count :3}
  ]);
  const handleAdditionCount = (index,action) => {
    const newTodos = [...todos]; //untuk mengcopy array ke dalam variabel baru

    action == 'plus' ? newTodos[index].count = newTodos[index].count + 1 : newTodos[index].count = newTodos[index].count - 1
    setTodos(newTodos);
  }
  return (
    // React Fragment
    <>
      <nav className="nav">
        <img className="nav-icon" src={shoppingIcon} alt="Shopping icon" />
        <h1 className="nav-title">Shopping List</h1>
      </nav>

      <section className="container">
        <form className="form">
          <input
          onChange={(e) => {setValue(e.target.value)}}
          value={value}
          className="input"
          type="text"
          placeholder="List"
          ></input>
          <button className="add-button" type="submit">add</button>
        </form>
        {todos.length > 0 ? (
          <div className="todos">
            {todos.map((todo,index,arr) => {
              return (
                <div key={index} className={`todo ${!(arr.length == index + 1) && 'todo-divider'}`}>
                  {todo.title}
                  <div className="todo-icon-wrapper">
                    <div className="todo-count">{todo.count}</div>
                    <button className="todo-action-button">
                      <img src={MinusIcon} alt="minus icon" />
                    </button>
                    <button className="todo-action-button" onClick={() => handleAdditionCount(index,'plus')}>
                      <img src={PlusIcon} alt="plus icon" />
                    </button>
                  </div>
                </div>
              )
            })}
          </div>
        ) :  (
          <div>Kosong</div>
        )}
      </section>
    </>
  );
}

export default App;
